	//監視ターゲットの取得
	const targets = document.querySelectorAll('[data-delighter]');
	//オブザーバーの作成
	const observer = new MutationObserver(aRecords => {
		//なんらかの処理を行なう
		//console.log('★',aRecords);
		//console.log('★',aRecords[0]['target']);
		aRecords.forEach(aRecord => {
			//console.log('★',aRecord.target,aRecord.attributeName,aRecord.oldValue);
			//console.log('★',aRecord.target);
			//console.log('★',aRecord.target['className'].split(' '));
			//console.log('★',aRecord.oldValue.split(' '));
			let nowClasses = aRecord.target['className'].split(' ');
			let oldClasses = aRecord.oldValue.split(' ');
			let word = "";
			let down = false;
			nowClasses.concat(oldClasses).forEach(item => {
				if (nowClasses.includes(item) && !oldClasses.includes(item)) {
					//console.log(`nowClassesに含まれていて、oldClassesに含まれていない : ${item}`);
					word = item;
					down = true;
				} else if (!nowClasses.includes(item) && oldClasses.includes(item)) {
					//console.log(`nowClassesに含まれていなくて、oldClassesに含まれていてる : ${item}`);
					word = item;
				}
			});
			if( word != '' && (word == 'started'||word == 'ended')){
				console.log('★',word,down,aRecord.target['id']);
			}
			
		});
	});

	//監視オプションの作成
	const options = {
		attributes: true,
		attributeFilter: ["class"],
		attributeOldValue : true
	};
	console.log( '★',targets.length );
	//監視の開始
	for(let i=0;i<targets.length;i++){
		//オブザーバーの作成
		observer.observe(targets[i], options);
	}
